"use strict";const products=[{id:1,title:"Notebook",price:2e3},{id:2,title:"Mouse",price:20},{id:3,title:"Keyboard",price:200},{id:4,title:"Gamepad",price:50}],renderProduct=(e,t="img/camera.jpg")=>`<div class="products__item">
              <img class="products__item-image" src=${t} alt="${e.title}" width="360" height="420">
              <h3 class="products__item-heading">${e.title}</h3>
              <p class="products__item-price">${e.price}$</p>
              <button class="buy-btn">Купить</button>
          </div>`,renderPage=e=>{e=e.map(e=>renderProduct(e)).join("");document.querySelector(".products").innerHTML=e};renderPage(products);